function filesizeHumanReadable ($size) {
	$scale = "KMGT";
	$scaleIdx = -1;

	$result = '';
	$s = $size;
	if ($s > 1024) {
            while ($s / 1024 > 1) {
                    $s = $s / 1024;
                    $scaleIdx++;
                    $result = round($s,2)."$scale[$scaleIdx]";
            }
	} else {
            $result = $s;
        }
	return $result."b, ".$size." bytes";
}

